templates.template:
{{ define "slack_title" }}
{{ if gt (len .Alerts.Firing) 0 }}⚠️ {{ len .Alerts.Firing }} alert(s) firing {{ end }}
{{ if gt (len .Alerts.Resolved) 0 }}✅ {{ len .Alerts.Resolved }} alert(s) resolved {{ end }}
{{ end }}

{{ define "slack_message" }}

{{ if gt (len .Alerts.Firing) 0 }}
{{ range .Alerts.Firing }}
{{ if match "Datasource.*" .Labels.alertname }}
{{ template "datasource_issue_firing" . }}
{{ break }}
{{ end }}
{{ end }}
{{ end }}

{{ if gt (len .Alerts.Firing) 0 }}
{{ range .Alerts.Firing }}
{{ if not (match "Datasource.*" .Labels.alertname) }}
{{ template "alert_firing" . }}
{{ end }}
{{ end }}
{{ end }}

{{ if gt (len .Alerts.Resolved) 0 }}
{{ range .Alerts.Resolved }}
{{ if match "Datasource.*" .Labels.alertname }}
{{ template "datasource_issue_resolved" . }}
{{ break }}
{{ end }}
{{ end }}
{{ end }}

{{ if gt (len .Alerts.Resolved) 0 }}
{{ range .Alerts.Resolved }}
{{ if not (match "Datasource.*" .Labels.alertname) }}
{{ template "alert_resolved" . }}
{{ end }}
{{ end }}
{{ end }}

{{ end }}

{{ define "datasource_issue_firing" }} 
*Datasource Error(s): * - Connection issue with Prometheus.
{{ end }}

{{ define "datasource_issue_resolved"}}
*Datasource error(s) resolved.*
{{ end }}

{{ define "alert_firing" }}
*{{ .Labels.alertname }}* - {{ .Annotations.summary }}
{{ end }}

{{ define "alert_resolved" }}
*{{ .Labels.alertname }}* - {{ .Annotations.summary }}
{{ end }}
